import React, { useState } from 'react';

const SubmissionLink = ({ linkTitle, dueDate }) => {
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [file, setFile] = useState(null);

  const openDialog = () => {
    setIsDialogOpen(true);
  };

  const closeDialog = () => {
    setIsDialogOpen(false);
    setFile(null);
  };

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    setFile(selectedFile);
  };

  const submitWork = () => {
    // Here you can implement the logic to handle the submitted file.
    // For example, you can upload the file to a server or perform any
    // necessary processing.
    console.log('File submitted:', file);

    closeDialog(); // Close the dialog after submission
  };

  return (
    <div>
      <p>{linkTitle}</p>
      <p>Due Date: {dueDate}</p>
      <button onClick={openDialog}>Submit Work</button>

      {isDialogOpen && (
        <div className="submission-dialog">
          <input type="file" onChange={handleFileChange} />
          <button onClick={submitWork}>Submit</button>
          <button onClick={closeDialog}>Cancel</button>
        </div>
      )}
    </div>
  );
};

const App = () => {
  return (
    <div>
      <h1>Employee Submission System</h1>
      <SubmissionLink linkTitle="Link 1" dueDate="2023-08-25" />
      <SubmissionLink linkTitle="Link 2" dueDate="2023-09-01" />
      <SubmissionLink linkTitle="Link 3" dueDate="2023-09-10" />
      <SubmissionLink linkTitle="Link 4" dueDate="2023-09-15" />
    </div>
  );
};

export default App;
