import React, { useState } from 'react';
import './SubmissionDialog.css';

const SubmissionDialog = ({ isOpen, onClose, onSubmit }) => {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle submission logic here, including the selectedFile
    onSubmit(selectedFile);
    setSelectedFile(null);
    onClose();
  };

  return (
    <div id="submission-dialog" className={`dialog ${isOpen ? 'open' : ''}`}>
      <div className="dialog-content">
        <span className="close-button" onClick={onClose}>&times;</span>
        <h2>Submit Your Work</h2>
        <form id="submission-form" onSubmit={handleSubmit}>
          <label htmlFor="work">Your Work:</label>
          <textarea id="work" name="work" rows="4" cols="50" required></textarea>

          <label htmlFor="fileUpload">Upload File:</label>
          <input
            type="file"
            id="fileUpload"
            accept=".pdf,.doc,.docx,.txt"
            onChange={handleFileChange}
          />

          <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
};

export default SubmissionDialog;
