import React, { useState } from 'react';
import SubmissionLink from './components/SubmissionLink';
import SubmissionDialog from './components/SubmissionDialog';
import './App.css';

function App() {
  const [dialogOpen, setDialogOpen] = useState(false);

  const openDialog = () => {
    setDialogOpen(true);
  };

  const closeDialog = () => {
    setDialogOpen(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle submission logic here
    closeDialog();
  };

  return (
    <div className="App">
      <nav>
        <ul>
          <li><SubmissionLink onClick={openDialog} /></li>
          {/* Add more SubmissionLink components as needed */}
        </ul>
      </nav>
      <SubmissionDialog isOpen={dialogOpen} onClose={closeDialog} onSubmit={handleSubmit} />
    </div>
  );
}

export default App;
