import React, { useState } from 'react';
import SubmissionLink from './components/SubmissionLink';
import SubmissionDialog from './components/SubmissionDialog';
import './App.css';

function App() {
  const [dialogOpen, setDialogOpen] = useState(false);
  const [selectedSubmission, setSelectedSubmission] = useState('');

  const openDialog = (submission) => {
    setDialogOpen(true);
    setSelectedSubmission(submission);
  };

  const closeDialog = () => {
    setDialogOpen(false);
  };

  const handleSubmit = (work, file) => {
    // Handle submission logic here, including work and file
    console.log(`Submitted: Work - ${work}, File - ${file ? file.name : 'No file'}`);
    closeDialog();
  };

  return (
    <div className="App">
      <nav>
        <ul>
          <li><SubmissionLink onClick={() => openDialog('Work 1')} /></li>
          <li><SubmissionLink onClick={() => openDialog('Work 2')} /></li>
          <li><SubmissionLink onClick={() => openDialog('Work 3')} /></li>
          <li><SubmissionLink onClick={() => openDialog('Work 4')} /></li>
        </ul>
      </nav>
      <SubmissionDialog
        isOpen={dialogOpen}
        onClose={closeDialog}
        onSubmit={handleSubmit}
        selectedSubmission={selectedSubmission}
      />
    </div>
  );
}

export default App;
