import React, { useState } from 'react';
import SubmissionLink from './components/SubmissionLink';
import SubmissionDialog from './components/SubmissionDialog';
import './App.css';
import SubmissionPage1 from './components/SubmissionPage1';
import SubmissionPage2 from './components/SubmissionPage2';
import SubmissionPage3 from './components/SubmissionPage3';
import SubmissionPage4 from './components/SubmissionPage4';

function App() {
  const [dialogOpen, setDialogOpen] = useState(false);
  const [selectedSubmission, setSelectedSubmission] = useState('');

  const openDialog = (submission) => {
    setDialogOpen(true);
    setSelectedSubmission(submission);
  };

  const closeDialog = () => {
    setDialogOpen(false);
  };

  const handleSubmit = (work, file) => {
    // Handle submission logic here, including work and file
    console.log(`Submitted: Work - ${work}, File - ${file ? file.name : 'No file'}`);
    closeDialog();
  };

  let submissionPageContent = null;
  switch (selectedSubmission) {
    case 'Work 1':
      submissionPageContent = <SubmissionPage1 />;
      break;
    case 'Work 2':
      submissionPageContent = <SubmissionPage2 />;
      break;
    case 'Work 3':
      submissionPageContent = <SubmissionPage3 />;
      break;
    case 'Work 4':
      submissionPageContent = <SubmissionPage4 />;
      break;
    default:
      submissionPageContent = null;
  }

  return (
    <div className="App">
      <nav>
        <ul>
          <li><SubmissionLink onClick={() => openDialog('Work 1')} /></li>
          <li><SubmissionLink onClick={() => openDialog('Work 2')} /></li>
          <li><SubmissionLink onClick={() => openDialog('Work 3')} /></li>
          <li><SubmissionLink onClick={() => openDialog('Work 4')} /></li>
        </ul>
      </nav>
      {submissionPageContent}
      <SubmissionDialog
        isOpen={dialogOpen}
        onClose={closeDialog}
        onSubmit={handleSubmit}
        selectedSubmission={selectedSubmission}
      />
    </div>
  );
}

export default App;
