import React, { useState } from 'react';
import './SubmissionDialog.css';

const SubmissionDialog = ({ isOpen, onClose, onSubmit, selectedSubmission }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [work, setWork] = useState('');

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Pass both work and selectedFile to the parent component
    onSubmit(work, selectedFile);
    setSelectedFile(null);
    setWork('');
    onClose();
  };
  // ... (similar to SubmissionDialog.js, adjust accordingly)
  return (
    <div id="submission-dialog" className={`dialog ${isOpen ? 'open' : ''}`}>
      <div className="dialog-content">
        <span className="close-button" onClick={onClose}>&times;</span>
        <h2>Submit Your Work</h2>
        <p>Submission: {selectedSubmission}</p>
        <form id="submission-form" onSubmit={handleSubmit}>
          <label htmlFor="work">Your Work:</label>
          <textarea
            id="work"
            name="work"
            rows="4"
            cols="50"
            value={work}
            onChange={(e) => setWork(e.target.value)}
            required
          />

          <label htmlFor="fileUpload">Upload File:</label>
          <input
            type="file"
            id="fileUpload"
            accept=".pdf,.doc,.docx,.txt"
            onChange={handleFileChange}
          />

          <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
};


export default SubmissionDialog1;
