import React, { useState } from 'react';
import './style.css';
import FileUploadDialog from './FileUploadDialog';

function App() {
  const [showDialog, setShowDialog] = useState(false);

  const openDialog = () => {
    setShowDialog(true);
  };

  const closeDialog = () => {
    setShowDialog(false);
  };

  return (
    <div className="app">
      <header className="header">
        <h1>Coffee Paste Making Company</h1>
      </header>
      <main className="main-content">
        <button className="upload-button" onClick={openDialog}>
          Upload File
        </button>
      </main>
      {showDialog && <FileUploadDialog onClose={closeDialog} />}
    </div>
  );
}

export default App;
